o={router:class{constructor(){this.routemklinks={}}AddRoute(t,e){this.routemklinks["#"+t]=e}On404(t){this.routemklinks[404]=t}start(){window.addEventListener("locationchange",function(){"string"==typeof this.routemklinks[location.hash]?document.querySelector("#op-view").innerHTML=this.routemklinks[location.hash]:"string"==typeof this.routemklinks[404]?document.querySelector("#op-view").innerHTML=this.routemklinks[404]:document.querySelector("#op-view").innerHTML="404; That page was not found."})}}};
